import React from 'react';

const QuickActions = ({ onAction }) => {
    const actions = [
        {
            id: 'add_product',
            icon: 'üì¶',
            title: 'Tambah Produk',
            description: 'Tambah produk baru ke inventory',
            color: 'primary'
        },
        {
            id: 'quick_sale',
            icon: 'üí∞',
            title: 'Quick Sale',
            description: 'Lakukan penjualan cepat',
            color: 'success'
        },
        {
            id: 'view_reports',
            icon: 'üìä',
            title: 'Lihat Laporan',
            description: 'Analisis performa penjualan',
            color: 'info'
        },
        {
            id: 'check_stock',
            icon: 'üîç',
            title: 'Cek Stok',
            description: 'Monitor level stok',
            color: 'warning'
        }
    ];

    return (
        <div className="row mb-4">
            <div className="col-12">
                <div className="card shadow">
                    <div className="card-header py-3">
                        <h6 className="m-0 font-weight-bold text-primary">
                            üöÄ Quick Actions
                        </h6>
                    </div>
                    <div className="card-body">
                        <div className="row">
                            {actions.map((action) => (
                                <div key={action.id} className="col-xl-3 col-md-6 mb-3">
                                    <div 
                                        className={`card action-card border-${action.color} h-100 cursor-pointer`}
                                        onClick={() => onAction(action.id)}
                                        style={{ cursor: 'pointer', transition: 'all 0.3s' }}
                                        onMouseEnter={(e) => {
                                            e.currentTarget.style.transform = 'translateY(-5px)';
                                            e.currentTarget.style.boxShadow = '0 8px 25px rgba(0,0,0,0.15)';
                                        }}
                                        onMouseLeave={(e) => {
                                            e.currentTarget.style.transform = 'translateY(0)';
                                            e.currentTarget.style.boxShadow = '';
                                        }}
                                    >
                                        <div className="card-body text-center">
                                            <div className={`h2 mb-3 text-${action.color}`}>
                                                {action.icon}
                                            </div>
                                            <h6 className="card-title">{action.title}</h6>
                                            <p className="card-text small text-muted">
                                                {action.description}
                                            </p>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default QuickActions;
