import React, { useState, useEffect } from 'react';
import { productAPI, transactionAPI } from '../../utils/api';
import { formatRupiah, getCategoryLabel, getAnimalTypeLabel } from '../../utils/helpers';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import { toast } from 'react-toastify';

const Forecasting = () => {
    const [products, setProducts] = useState([]);
    const [forecasts, setForecasts] = useState({});
    const [loading, setLoading] = useState(true);
    const [calculating, setCalculating] = useState(false);

    useEffect(() => {
        loadProducts();
    }, []);

    const loadProducts = async () => {
        try {
            setLoading(true);
            const data = await productAPI.getAll();
            setProducts(data);
        } catch (error) {
            toast.error('Gagal memuat data produk');
        } finally {
            setLoading(false);
        }
    };

    const calculateForecast = async (productId) => {
        try {
            setCalculating(true);
            const forecast = await transactionAPI.getForecast(productId);
            setForecasts(prev => ({
                ...prev,
                [productId]: forecast
            }));
        } catch (error) {
            toast.error('Gagal menghitung forecast');
        } finally {
            setCalculating(false);
        }
    };

    const calculateAllForecasts = async () => {
        try {
            setCalculating(true);
            const newForecasts = {};
            
            for (const product of products) {
                try {
                    const forecast = await transactionAPI.getForecast(product.id);
                    newForecasts[product.id] = forecast;
                } catch (error) {
                    console.error(`Forecast error for product ${product.id}:`, error);
                    newForecasts[product.id] = {
                        forecast: 0,
                        suggestion: 0,
                        message: 'Gagal menghitung forecast'
                    };
                }
            }
            
            setForecasts(newForecasts);
            toast.success('Forecast semua produk berhasil dihitung');
        } catch (error) {
            toast.error('Gagal menghitung forecast untuk beberapa produk');
        } finally {
            setCalculating(false);
        }
    };

    const getSuggestionColor = (suggestion) => {
        if (suggestion === 0) return 'text-success';
        if (suggestion <= 10) return 'text-warning';
        return 'text-danger';
    };

    const getSuggestionIcon = (suggestion) => {
        if (suggestion === 0) return '‚úÖ';
        if (suggestion <= 10) return '‚ö†Ô∏è';
        return 'üö®';
    };

    if (loading) {
        return <LoadingSpinner text="Memuat data forecasting..." />;
    }

    return (
        <div className="fade-in">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <div>
                    <h1 className="h2">üîÆ Forecasting & Rekomendasi</h1>
                    <p className="text-muted">
                        Prediksi permintaan bulan depan menggunakan metode Moving Average (3 bulan)
                    </p>
                </div>
                <div className="btn-toolbar mb-2 mb-md-0">
                    <button 
                        className="btn btn-sm btn-petshop"
                        onClick={calculateAllForecasts}
                        disabled={calculating || products.length === 0}
                    >
                        {calculating ? (
                            <>
                                <span className="spinner-border spinner-border-sm me-2"></span>
                                Menghitung...
                            </>
                        ) : (
                            'üìä Hitung Semua Forecast'
                        )}
                    </button>
                </div>
            </div>

            {/* Explanation Card */}
            <div className="row mb-4">
                <div className="col-12">
                    <div className="card bg-light border-0">
                        <div className="card-body">
                            <h6>üìù Cara Kerja Forecasting:</h6>
                            <ul className="mb-0">
                                <li>Menggunakan data penjualan 3 bulan terakhir</li>
                                <li>Metode: <strong>Simple Moving Average</strong></li>
                                <li>Rumus: (Total penjualan 3 bulan) √∑ 3</li>
                                <li>Rekomendasi beli = Forecast - Stok saat ini</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>

            {/* Products Table */}
            <div className="card shadow">
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table table-hover">
                            <thead className="table-light">
                                <tr>
                                    <th>Produk</th>
                                    <th>Kategori</th>
                                    <th>Stok Saat Ini</th>
                                    <th>Forecast Bulan Depan</th>
                                    <th>Rekomendasi Beli</th>
                                    <th>Status</th>
                                    <th>Aksi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {products.length === 0 ? (
                                    <tr>
                                        <td colSpan="7" className="text-center py-4">
                                            <div className="text-muted">
                                                Belum ada produk. Tambah produk terlebih dahulu.
                                            </div>
                                        </td>
                                    </tr>
                                ) : (
                                    products.map((product) => {
                                        const forecast = forecasts[product.id];
                                        const hasForecast = forecast && !forecast.message;
                                        
                                        return (
                                            <tr key={product.id}>
                                                <td>
                                                    <strong>{product.name}</strong>
                                                    <br />
                                                    <small className="text-muted">
                                                        {product.sku_code}
                                                    </small>
                                                </td>
                                                <td>
                                                    {getCategoryLabel(product.category)}
                                                    <br />
                                                    <small>
                                                        {getAnimalTypeLabel(product.animal_type)}
                                                    </small>
                                                </td>
                                                <td>
                                                    <span className="fw-bold">{product.current_stock}</span>
                                                </td>
                                                <td>
                                                    {hasForecast ? (
                                                        <span className="fw-bold text-primary">
                                                            {forecast.forecast} pcs
                                                        </span>
                                                    ) : (
                                                        <span className="text-muted">-</span>
                                                    )}
                                                </td>
                                                <td>
                                                    {hasForecast ? (
                                                        <span className={`fw-bold ${getSuggestionColor(forecast.suggestion)}`}>
                                                            {getSuggestionIcon(forecast.suggestion)} {forecast.suggestion} pcs
                                                        </span>
                                                    ) : (
                                                        <span className="text-muted">-</span>
                                                    )}
                                                </td>
                                                <td>
                                                    {hasForecast ? (
                                                        forecast.suggestion === 0 ? (
                                                            <span className="badge bg-success">Cukup</span>
                                                        ) : forecast.suggestion <= 10 ? (
                                                            <span className="badge bg-warning">Perlu Tambah</span>
                                                        ) : (
                                                            <span className="badge bg-danger">Segera Restock</span>
                                                        )
                                                    ) : (
                                                        <span className="badge bg-secondary">Belum Dihitung</span>
                                                    )}
                                                </td>
                                                <td>
                                                    <button
                                                        className="btn btn-outline-primary btn-sm"
                                                        onClick={() => calculateForecast(product.id)}
                                                        disabled={calculating}
                                                        title="Hitung Forecast"
                                                    >
                                                        {calculating && forecasts[product.id] ? (
                                                            <span className="spinner-border spinner-border-sm"></span>
                                                        ) : (
                                                            'üìà'
                                                        )}
                                                    </button>
                                                </td>
                                            </tr>
                                        );
                                    })
                                )}
                            </tbody>
                        </table>
                    </div>

                    {/* Legend */}
                    <div className="row mt-4">
                        <div className="col-md-8">
                            <h6>Keterangan:</h6>
                            <div className="row">
                                <div className="col-md-4">
                                    <span className="badge bg-success me-2">‚úÖ</span>
                                    <small>Cukup: Tidak perlu tambah stok</small>
                                </div>
                                <div className="col-md-4">
                                    <span className="badge bg-warning me-2">‚ö†Ô∏è</span>
                                    <small>Perlu Tambah: Restock 1-10 pcs</small>
                                </div>
                                <div className="col-md-4">
                                    <span className="badge bg-danger me-2">üö®</span>
                                    <small>Segera Restock: Butuh banyak stok</small>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Forecasting Details */}
            {Object.keys(forecasts).length > 0 && (
                <div className="row mt-4">
                    <div className="col-12">
                        <div className="card shadow">
                            <div className="card-header">
                                <h6 className="m-0 font-weight-bold text-primary">
                                    üìã Detail Perhitungan Forecast
                                </h6>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    {products.filter(p => forecasts[p.id]?.sales_data).map(product => {
                                        const forecast = forecasts[product.id];
                                        return (
                                            <div key={product.id} className="col-lg-6 mb-3">
                                                <div className="card bg-light">
                                                    <div className="card-body">
                                                        <h6>{product.name}</h6>
                                                        <table className="table table-sm">
                                                            <thead>
                                                                <tr>
                                                                    <th>Bulan</th>
                                                                    <th>Penjualan</th>
                                                                </tr>
                                                            </thead>
                                                            <tbody>
                                                                {forecast.sales_data.map((sale, index) => (
                                                                    <tr key={index}>
                                                                        <td>{sale.month}/{sale.year}</td>
                                                                        <td>{sale.total_sold} pcs</td>
                                                                    </tr>
                                                                ))}
                                                            </tbody>
                                                            <tfoot>
                                                                <tr className="table-primary">
                                                                    <td><strong>Rata-rata (Forecast)</strong></td>
                                                                    <td><strong>{forecast.forecast} pcs</strong></td>
                                                                </tr>
                                                            </tfoot>
                                                        </table>
                                                    </div>
                                                </div>
                                            </div>
                                        );
                                    })}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Forecasting;
