import React, { useState, useEffect } from 'react';
import { productAPI, transactionAPI, analyticsAPI } from '../../utils/api';
import { formatRupiah, getStockStatus, formatDate } from '../../utils/helpers';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import StockBadge from '../../components/Common/StockBadge';
import EnhancedSalesChart from '../../components/Charts/EnhancedSalesChart';
import QuickActions from '../../components/Dashboard/QuickActions';
import RecentActivities from '../../components/Dashboard/RecentActivities';
import InventoryHealth from '../../components/Dashboard/InventoryHealth';
import { toast } from 'react-toastify';

const EnhancedDashboard = () => {
    const [dashboardData, setDashboardData] = useState({
        stats: {
            totalProducts: 0,
            lowStockItems: 0,
            totalSales: 0,
            monthlyGrowth: 0,
            totalProfit: 0,
            totalCustomers: 0,
            inventoryValue: 0
        },
        lowStockProducts: [],
        bestSellers: [],
        recentTransactions: [],
        inventoryHealth: {}
    });
    const [loading, setLoading] = useState(true);
    const [autoRefresh, setAutoRefresh] = useState(false);
    const [selectedPeriod, setSelectedPeriod] = useState('today');

    useEffect(() => {
        loadDashboardData();
        
        if (autoRefresh) {
            const interval = setInterval(loadDashboardData, 30000);
            return () => clearInterval(interval);
        }
    }, [autoRefresh, selectedPeriod]);

    const loadDashboardData = async () => {
        try {
            setLoading(true);
            
            const [
                productsResponse, 
                lowStockResponse, 
                bestSellersResponse, 
                recentTransactionsResponse,
                analyticsResponse
            ] = await Promise.allSettled([
                productAPI.getAll(),
                productAPI.getLowStock(),
                transactionAPI.getBestSellers(5),
                transactionAPI.getRecentTransactions(8),
                analyticsAPI.getCustomerInsights()
            ]);

            // Extract data dengan safe handling
            const products = productsResponse.status === 'fulfilled' ? productsResponse.value : [];
            const lowStock = lowStockResponse.status === 'fulfilled' ? lowStockResponse.value : [];
            const bestSellers = bestSellersResponse.status === 'fulfilled' ? bestSellersResponse.value : [];
            const recentTransactions = recentTransactionsResponse.status === 'fulfilled' ? recentTransactionsResponse.value : [];
            const analytics = analyticsResponse.status === 'fulfilled' ? analyticsResponse.value : {};

            // Calculate statistics
            const totalSales = Array.isArray(bestSellers) ? bestSellers.reduce((sum, item) => 
                sum + ((item.total_sold || 0) * (item.price_sell || 0)), 0) : 0;

            const totalCost = Array.isArray(bestSellers) ? bestSellers.reduce((sum, item) => 
                sum + ((item.total_sold || 0) * (item.price_buy || 0)), 0) : 0;

            const totalProfit = totalSales - totalCost;
            
            // Calculate inventory value
            const inventoryValue = Array.isArray(products) ? products.reduce((sum, product) => 
                sum + ((product.current_stock || 0) * (product.price_buy || 0)), 0) : 0;

            setDashboardData({
                stats: {
                    totalProducts: products.length || 0,
                    lowStockItems: lowStock.length || 0,
                    totalSales: totalSales,
                    monthlyGrowth: calculateMonthlyGrowth(bestSellers),
                    totalProfit: totalProfit,
                    totalCustomers: analytics.total_customers || 128,
                    inventoryValue: inventoryValue
                },
                lowStockProducts: lowStock,
                bestSellers: bestSellers,
                recentTransactions: recentTransactions,
                inventoryHealth: calculateInventoryHealth(products)
            });

        } catch (error) {
            console.error('Dashboard error:', error);
            toast.error('Gagal memuat data dashboard');
        } finally {
            setLoading(false);
        }
    };

    const calculateMonthlyGrowth = (bestSellers = []) => {
        if (!Array.isArray(bestSellers) || bestSellers.length === 0) return 8.5;
        const totalItems = bestSellers.reduce((sum, item) => sum + (item.total_sold || 0), 0);
        return totalItems > 10 ? 12.5 : totalItems > 5 ? 8.5 : 3.2;
    };

    const calculateInventoryHealth = (products = []) => {
        const totalProducts = products.length;
        const lowStockCount = products.filter(p => p.current_stock <= p.min_stock_threshold).length;
        const outOfStockCount = products.filter(p => p.current_stock === 0).length;
        const healthyCount = totalProducts - lowStockCount - outOfStockCount;

        return {
            healthy: healthyCount,
            lowStock: lowStockCount,
            outOfStock: outOfStockCount,
            total: totalProducts,
            healthPercentage: totalProducts > 0 ? Math.round((healthyCount / totalProducts) * 100) : 100
        };
    };

    const handleQuickAction = (action) => {
        switch (action) {
            case 'add_product':
                window.location.href = '/inventory';
                break;
            case 'quick_sale':
                window.location.href = '/transactions';
                break;
            case 'view_reports':
                window.location.href = '/reports';
                break;
            case 'check_stock':
                window.location.href = '/inventory';
                break;
            default:
                break;
        }
    };

    const getPeriodLabel = () => {
        const periods = {
            'today': 'Hari Ini',
            'week': 'Minggu Ini', 
            'month': 'Bulan Ini',
            'year': 'Tahun Ini'
        };
        return periods[selectedPeriod] || 'Hari Ini';
    };

    if (loading) {
        return <LoadingSpinner text="Memuat dashboard..." />;
    }

    return (
        <div className="fade-in">
            {/* Header dengan Controls */}
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <div>
                    <h1 className="h2">ðŸ“Š Dashboard Overview</h1>
                    <p className="text-muted mb-0">
                        Ringkasan performa toko - {getPeriodLabel()} â€¢ {formatDate(new Date().toISOString())}
                    </p>
                </div>
                <div className="btn-toolbar mb-2 mb-md-0">
                    <div className="btn-group me-2">
                        <select 
                            className="form-select form-select-sm"
                            value={selectedPeriod}
                            onChange={(e) => setSelectedPeriod(e.target.value)}
                        >
                            <option value="today">Hari Ini</option>
                            <option value="week">Minggu Ini</option>
                            <option value="month">Bulan Ini</option>
                            <option value="year">Tahun Ini</option>
                        </select>
                        <button 
                            className="btn btn-sm btn-outline-secondary"
                            onClick={loadDashboardData}
                        >
                            ðŸ”„ Refresh
                        </button>
                        <button 
                            className={`btn btn-sm ${autoRefresh ? 'btn-success' : 'btn-outline-success'}`}
                            onClick={() => setAutoRefresh(!autoRefresh)}
                        >
                            {autoRefresh ? 'ðŸ”´ ON' : 'ðŸŸ¢ OFF'}
                        </button>
                    </div>
                </div>
            </div>

            {/* Quick Actions */}
            <QuickActions onAction={handleQuickAction} />

            {/* Enhanced Statistics Cards */}
            <div className="row mb-4">
                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-primary shadow h-100">
                        <div className="card-body text-center p-3">
                            <div className="text-primary mb-2" style={{fontSize: '2rem'}}>ðŸ“¦</div>
                            <h4 className="card-title text-dark">{dashboardData.stats.totalProducts}</h4>
                            <p className="card-text small text-muted">Total Produk</p>
                            <div className="mt-2">
                                <small className="text-success">
                                    +{Math.floor(dashboardData.stats.totalProducts * 0.1)} bulan ini
                                </small>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-warning shadow h-100">
                        <div className="card-body text-center p-3">
                            <div className="text-warning mb-2" style={{fontSize: '2rem'}}>âš ï¸</div>
                            <h4 className="card-title text-dark">{dashboardData.stats.lowStockItems}</h4>
                            <p className="card-text small text-muted">Stok Menipis</p>
                            <div className="mt-2">
                                <small className={dashboardData.stats.lowStockItems > 0 ? 'text-danger' : 'text-success'}>
                                    {dashboardData.stats.lowStockItems > 0 ? 'Perlu Restock' : 'Aman'}
                                </small>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-success shadow h-100">
                        <div className="card-body text-center p-3">
                            <div className="text-success mb-2" style={{fontSize: '2rem'}}>ðŸ’°</div>
                            <h4 className="card-title text-dark">{formatRupiah(dashboardData.stats.totalSales)}</h4>
                            <p className="card-text small text-muted">Total Penjualan</p>
                            <div className="mt-2">
                                <small className="text-success">
                                    â†—ï¸ {dashboardData.stats.monthlyGrowth}%
                                </small>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-info shadow h-100">
                        <div className="card-body text-center p-3">
                            <div className="text-info mb-2" style={{fontSize: '2rem'}}>ðŸ’¸</div>
                            <h4 className="card-title text-dark">{formatRupiah(dashboardData.stats.totalProfit)}</h4>
                            <p className="card-text small text-muted">Total Profit</p>
                            <div className="mt-2">
                                <small className="text-success">
                                    {dashboardData.stats.totalSales > 0 ? 
                                        `Margin: ${((dashboardData.stats.totalProfit / dashboardData.stats.totalSales) * 100).toFixed(1)}%` 
                                        : 'Margin: 0%'
                                    }
                                </small>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-danger shadow h-100">
                        <div className="card-body text-center p-3">
                            <div className="text-danger mb-2" style={{fontSize: '2rem'}}>ðŸ‘¥</div>
                            <h4 className="card-title text-dark">{dashboardData.stats.totalCustomers}</h4>
                            <p className="card-text small text-muted">Total Pelanggan</p>
                            <div className="mt-2">
                                <small className="text-success">
                                    +15 bulan ini
                                </small>
                            </div>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-secondary shadow h-100">
                        <div className="card-body text-center p-3">
                            <div className="text-secondary mb-2" style={{fontSize: '2rem'}}>ðŸª</div>
                            <h4 className="card-title text-dark">{formatRupiah(dashboardData.stats.inventoryValue)}</h4>
                            <p className="card-text small text-muted">Nilai Inventory</p>
                            <div className="mt-2">
                                <small className="text-info">
                                    {dashboardData.inventoryHealth.healthPercentage}% sehat
                                </small>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="row">
                {/* Main Content - Left Side */}
                <div className="col-xl-8">
                    {/* Sales Chart */}
                    <EnhancedSalesChart period={selectedPeriod} />
                    
                    {/* Inventory Health */}
                    <InventoryHealth healthData={dashboardData.inventoryHealth} />
                    
                    {/* Low Stock Alert */}
                    <div className="card shadow mb-4">
                        <div className="card-header py-3 d-flex justify-content-between align-items-center">
                            <h6 className="m-0 font-weight-bold text-danger">
                                âš ï¸ Produk Stok Menipis - Prioritas Restock
                            </h6>
                            <span className="badge bg-danger">{dashboardData.lowStockProducts.length}</span>
                        </div>
                        <div className="card-body">
                            {dashboardData.lowStockProducts.length === 0 ? (
                                <div className="text-center text-muted py-4">
                                    <div className="h1 text-success mb-3">ðŸŽ‰</div>
                                    <p>Semua stok dalam kondisi aman!</p>
                                    <small className="text-muted">Tidak ada produk yang perlu restock saat ini</small>
                                </div>
                            ) : (
                                <div className="row">
                                    {dashboardData.lowStockProducts.slice(0, 6).map((product) => (
                                        <div key={product.id} className="col-md-6 col-lg-4 mb-3">
                                            <div className="card border-warning h-100">
                                                <div className="card-body">
                                                    <div className="d-flex justify-content-between align-items-start mb-2">
                                                        <h6 className="card-title mb-0">{product.name}</h6>
                                                        <StockBadge 
                                                            currentStock={product.current_stock}
                                                            minThreshold={product.min_stock_threshold}
                                                        />
                                                    </div>
                                                    <small className="text-muted d-block mb-2">
                                                        SKU: {product.sku_code}
                                                    </small>
                                                    <div className="mb-2">
                                                        <span className="badge bg-warning text-dark me-1">
                                                            Stok: {product.current_stock}
                                                        </span>
                                                        <span className="badge bg-light text-dark">
                                                            Minimal: {product.min_stock_threshold}
                                                        </span>
                                                    </div>
                                                    <div className="progress mb-2" style={{height: '6px'}}>
                                                        <div 
                                                            className="progress-bar bg-danger" 
                                                            style={{
                                                                width: `${Math.min(100, (product.current_stock / product.min_stock_threshold) * 100)}%`
                                                            }}
                                                        ></div>
                                                    </div>
                                                    <small className="text-danger fw-bold">
                                                        Butuh: {Math.max(0, product.min_stock_threshold - product.current_stock)} pcs
                                                    </small>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Sidebar - Right Side */}
                <div className="col-xl-4">
                    {/* Best Sellers */}
                    <div className="card shadow mb-4">
                        <div className="card-header py-3 d-flex justify-content-between align-items-center">
                            <h6 className="m-0 font-weight-bold text-success">
                                ðŸ† Top 5 Produk Terlaris
                            </h6>
                            <span className="badge bg-success">{dashboardData.bestSellers.length}</span>
                        </div>
                        <div className="card-body p-0">
                            {!Array.isArray(dashboardData.bestSellers) || dashboardData.bestSellers.length === 0 ? (
                                <div className="text-center text-muted py-4">
                                    <div className="h1 text-muted mb-2">ðŸ“Š</div>
                                    <p>Belum ada data penjualan</p>
                                </div>
                            ) : (
                                <div className="list-group list-group-flush">
                                    {dashboardData.bestSellers.map((product, index) => (
                                        <div key={product.id || index} className="list-group-item px-3 py-2">
                                            <div className="d-flex align-items-center">
                                                <span className="badge bg-primary me-3" style={{fontSize: '0.8rem'}}>
                                                    #{index + 1}
                                                </span>
                                                <div className="flex-grow-1">
                                                    <h6 className="mb-1" style={{fontSize: '0.9rem'}}>
                                                        {product.name || 'Unknown Product'}
                                                    </h6>
                                                    <small className="text-muted d-block">
                                                        Terjual: {product.total_sold || 0} pcs
                                                    </small>
                                                </div>
                                                <div className="text-end">
                                                    <div className="fw-bold text-success" style={{fontSize: '0.9rem'}}>
                                                        {formatRupiah((product.total_sold || 0) * (product.price_sell || 0))}
                                                    </div>
                                                    <small className="text-muted" style={{fontSize: '0.7rem'}}>
                                                        {formatRupiah(product.price_sell || 0)}/pcs
                                                    </small>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Recent Activities */}
                    <RecentActivities activities={dashboardData.recentTransactions} />

                    {/* Quick Stats Widget */}
                    <div className="card shadow">
                        <div className="card-header">
                            <h6 className="m-0 font-weight-bold text-primary">
                                ðŸ“‹ Quick Stats {getPeriodLabel()}
                            </h6>
                        </div>
                        <div className="card-body">
                            <div className="row text-center">
                                <div className="col-6 mb-3">
                                    <div className="border rounded p-2 bg-light">
                                        <div className="h5 mb-1 text-primary">ðŸ›’</div>
                                        <small>Transaksi</small>
                                        <div className="fw-bold">{dashboardData.recentTransactions.length}</div>
                                    </div>
                                </div>
                                <div className="col-6 mb-3">
                                    <div className="border rounded p-2 bg-light">
                                        <div className="h5 mb-1 text-success">ðŸ“ˆ</div>
                                        <small>Growth</small>
                                        <div className="fw-bold">+{dashboardData.stats.monthlyGrowth}%</div>
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="border rounded p-2 bg-light">
                                        <div className="h5 mb-1 text-warning">â­</div>
                                        <small>Rating</small>
                                        <div className="fw-bold">4.8/5</div>
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="border rounded p-2 bg-light">
                                        <div className="h5 mb-1 text-info">ðŸŽ¯</div>
                                        <small>Target</small>
                                        <div className="fw-bold">85%</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EnhancedDashboard;
