import React, { useEffect, useState } from 'react';
import { userAPI, handleApiError } from '../../utils/api';
import { useAuth } from '../../context/AuthContext';

const UserManagement = () => {
    const { user: authUser } = useAuth();
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(false);
    const [form, setForm] = useState({ id: null, username: '', email: '', password: '' });
    const [role, setRole] = useState('staff');
    const [editing, setEditing] = useState(false);
    const [error, setError] = useState(null);

    const loadUsers = async () => {
        setLoading(true);
        try {
            const data = await userAPI.getAll();
            setUsers(data);
        } catch (err) {
            console.error(err);
            setError('Gagal memuat pengguna');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        loadUsers();
    }, []);

    const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

    const handleCreate = async (e) => {
        e.preventDefault();
        setError(null);
        try {
            await userAPI.create({ username: form.username, password: form.password, email: form.email, role });
            setForm({ id: null, username: '', email: '', password: '' });
            setRole('staff');
            loadUsers();
        } catch (err) {
            console.error(err);
            const result = handleApiError(err);
            setError(result.message || 'Gagal membuat pengguna');
        }
    };

    const startEdit = (u) => {
        setEditing(true);
        setForm({ id: u.id, username: u.username, email: u.email, password: '' });
        setRole(u.role || 'staff');
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        setError(null);
        try {
            await userAPI.update({ ...form, role });
            setForm({ id: null, username: '', email: '', password: '' });
            setRole('staff');
            setEditing(false);
            loadUsers();
        } catch (err) {
            console.error(err);
            const result = handleApiError(err);
            setError(result.message || 'Gagal memperbarui pengguna');
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Hapus pengguna ini?')) return;
        try {
            await userAPI.delete(id);
            loadUsers();
        } catch (err) {
            console.error(err);
            alert('Gagal menghapus pengguna');
        }
    };

    const [showPasswordModal, setShowPasswordModal] = useState(false);
    const [passwordToShow, setPasswordToShow] = useState('');
    const [loadingPassword, setLoadingPassword] = useState(false);

    const handleShowPassword = async (id) => {
        setLoadingPassword(true);
        setPasswordToShow('');
        setShowPasswordModal(true);
        try {
            const res = await userAPI.showPassword(id);
            // response shape: { success: true, password: '...' }
            if (res && res.password) {
                setPasswordToShow(res.password);
            } else if (res && res.message) {
                setPasswordToShow('Error: ' + res.message);
            } else {
                setPasswordToShow('Tidak ada password');
            }
        } catch (err) {
            console.error(err);
            setPasswordToShow('Gagal mengambil password (cek permission)');
        } finally {
            setLoadingPassword(false);
        }
    };

    return (
        <div className="fade-in">
            <div className="d-flex justify-content-between align-items-center mb-3">
                <h3>Manajemen Pengguna</h3>
            </div>

            <div className="row">
                <div className="col-md-5">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">{editing ? 'Edit Pengguna' : 'Tambah Pengguna'}</h5>
                            {error && <div className="alert alert-danger">{error}</div>}
                            <form onSubmit={editing ? handleUpdate : handleCreate}>
                                <div className="mb-2">
                                    <label className="form-label">Username</label>
                                    <input name="username" value={form.username} onChange={handleChange} className="form-control" required />
                                </div>
                                <div className="mb-2">
                                    <label className="form-label">Email</label>
                                    <input name="email" value={form.email} onChange={handleChange} className="form-control" />
                                </div>
                                <div className="mb-2">
                                    <label className="form-label">Role</label>
                                    <select className="form-select" value={role} onChange={(e) => setRole(e.target.value)}>
                                        <option value="staff">Staff</option>
                                        <option value="admin">Admin</option>
                                    </select>
                                </div>
                                <div className="mb-2">
                                    <label className="form-label">Password {editing ? '(kosongkan jika tidak diubah)' : ''}</label>
                                    <input name="password" value={form.password} onChange={handleChange} className="form-control" type="password" required={!editing} />
                                </div>
                                <div className="d-flex gap-2">
                                    <button className="btn btn-primary" type="submit">{editing ? 'Simpan' : 'Buat'}</button>
                                    {editing && <button type="button" className="btn btn-secondary" onClick={() => { setEditing(false); setForm({ id: null, username: '', email: '', password: '' }); }}>Batal</button>}
                                </div>
                            </form>
                        </div>
                    </div>
                </div>

                <div className="col-md-7">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Daftar Pengguna</h5>
                            {loading ? (
                                <div>Memuat...</div>
                            ) : (
                                <div className="table-responsive">
                                    <table className="table table-hover">
                                        <thead>
                                            <tr>
                                                <th>ID</th>
                                                <th>Username</th>
                                                <th>Email</th>
                                                <th>Created At</th>
                                                <th>Aksi</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {users.map(u => (
                                                <tr key={u.id}>
                                                    <td>{u.id}</td>
                                                    <td>{u.username}</td>
                                                    <td>{u.email}</td>
                                                    <td>{u.created_at}</td>
                                                    <td>
                                                            <button className="btn btn-sm btn-outline-primary me-2" onClick={() => startEdit(u)}>Edit</button>
                                                            {/* show button only to admin users */}
                                                            {authUser?.role === 'admin' && (
                                                                <button className="btn btn-sm btn-outline-secondary me-2" onClick={() => handleShowPassword(u.id)}>Tampilkan</button>
                                                            )}
                                                            <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(u.id)}>Hapus</button>
                                                    </td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
            <PasswordModal show={showPasswordModal} onClose={() => setShowPasswordModal(false)} password={passwordToShow} loading={loadingPassword} />
        </div>
    );
};

// Simple modal markup (no external modal library required)
function PasswordModal({ show, onClose, password, loading }) {
    if (!show) return null;
    return (
        <div className="modal show d-block" tabIndex="-1" role="dialog">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Tampilkan Password</h5>
                        <button type="button" className="btn-close" aria-label="Close" onClick={onClose}></button>
                    </div>
                    <div className="modal-body">
                        {loading ? <div>Memuat...</div> : <pre style={{wordBreak: 'break-all'}}>{password}</pre>}
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" onClick={onClose}>Tutup</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default UserManagement;
