import React, { useState, useEffect } from 'react';
import { formatRupiah } from '../../utils/helpers';

const EnhancedSalesChart = () => {
    const [timeRange, setTimeRange] = useState('month');
    const [chartData, setChartData] = useState([]);

    useEffect(() => {
        loadChartData();
    }, [timeRange]);

    const loadChartData = () => {
        // Simulate different data based on time range
        let data = [];
        
        switch (timeRange) {
            case 'week':
                data = [
                    { day: 'Sen', sales: 450000, profit: 120000 },
                    { day: 'Sel', sales: 520000, profit: 150000 },
                    { day: 'Rab', sales: 480000, profit: 130000 },
                    { day: 'Kam', sales: 610000, profit: 180000 },
                    { day: 'Jum', sales: 720000, profit: 220000 },
                    { day: 'Sab', sales: 850000, profit: 280000 },
                    { day: 'Min', sales: 780000, profit: 250000 }
                ];
                break;
            case 'month':
                data = [
                    { week: 'Minggu 1', sales: 2800000, profit: 800000 },
                    { week: 'Minggu 2', sales: 3200000, profit: 950000 },
                    { week: 'Minggu 3', sales: 3500000, profit: 1100000 },
                    { week: 'Minggu 4', sales: 4100000, profit: 1300000 }
                ];
                break;
            case 'year':
                data = [
                    { month: 'Jan', sales: 12500000, profit: 3800000 },
                    { month: 'Feb', sales: 13200000, profit: 4200000 },
                    { month: 'Mar', sales: 12800000, profit: 4000000 },
                    { month: 'Apr', sales: 14500000, profit: 4800000 },
                    { month: 'Mei', sales: 15200000, profit: 5200000 },
                    { month: 'Jun', sales: 14800000, profit: 5000000 }
                ];
                break;
            default:
                data = [];
        }
        
        setChartData(data);
    };

    const maxSales = Math.max(...chartData.map(item => item.sales));
    const maxProfit = Math.max(...chartData.map(item => item.profit));

    return (
        <div className="card shadow mb-4">
            <div className="card-header py-3 d-flex justify-content-between align-items-center">
                <h6 className="m-0 font-weight-bold text-primary">
                    ðŸ“ˆ Grafik Penjualan & Profit
                </h6>
                <div className="btn-group btn-group-sm">
                    <button
                        className={`btn ${timeRange === 'week' ? 'btn-primary' : 'btn-outline-primary'}`}
                        onClick={() => setTimeRange('week')}
                    >
                        Minggu
                    </button>
                    <button
                        className={`btn ${timeRange === 'month' ? 'btn-primary' : 'btn-outline-primary'}`}
                        onClick={() => setTimeRange('month')}
                    >
                        Bulan
                    </button>
                    <button
                        className={`btn ${timeRange === 'year' ? 'btn-primary' : 'btn-outline-primary'}`}
                        onClick={() => setTimeRange('year')}
                    >
                        Tahun
                    </button>
                </div>
            </div>
            <div className="card-body">
                <div className="chart-container">
                    {chartData.map((item, index) => {
                        const salesPercentage = (item.sales / maxSales) * 80;
                        const profitPercentage = (item.profit / maxProfit) * 80;
                        
                        return (
                            <div key={index} className="mb-4">
                                <div className="d-flex justify-content-between mb-2">
                                    <small className="fw-bold">
                                        {item.day || item.week || item.month}
                                    </small>
                                    <div>
                                        <small className="text-success me-3">
                                            Sales: {formatRupiah(item.sales)}
                                        </small>
                                        <small className="text-primary">
                                            Profit: {formatRupiah(item.profit)}
                                        </small>
                                    </div>
                                </div>
                                
                                <div className="d-flex align-items-end mb-1">
                                    <div 
                                        className="bg-success rounded me-1"
                                        style={{ 
                                            height: '20px', 
                                            width: `${salesPercentage}%`,
                                            opacity: 0.7 
                                        }}
                                        title={`Sales: ${formatRupiah(item.sales)}`}
                                    ></div>
                                    <div 
                                        className="bg-primary rounded"
                                        style={{ 
                                            height: '16px', 
                                            width: `${profitPercentage}%`,
                                            opacity: 0.7 
                                        }}
                                        title={`Profit: ${formatRupiah(item.profit)}`}
                                    ></div>
                                </div>
                                
                                <div className="d-flex justify-content-between">
                                    <small className="text-success">
                                        Sales
                                    </small>
                                    <small className="text-primary">
                                        Profit
                                    </small>
                                </div>
                            </div>
                        );
                    })}
                </div>
                
                {/* Summary Statistics */}
                <div className="row mt-4 text-center">
                    <div className="col-md-3">
                        <div className="card bg-light">
                            <div className="card-body py-2">
                                <small>Total Sales</small>
                                <h6 className="text-success mb-0">
                                    {formatRupiah(chartData.reduce((sum, item) => sum + item.sales, 0))}
                                </h6>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card bg-light">
                            <div className="card-body py-2">
                                <small>Total Profit</small>
                                <h6 className="text-primary mb-0">
                                    {formatRupiah(chartData.reduce((sum, item) => sum + item.profit, 0))}
                                </h6>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card bg-light">
                            <div className="card-body py-2">
                                <small>Profit Margin</small>
                                <h6 className="text-warning mb-0">
                                    {((chartData.reduce((sum, item) => sum + item.profit, 0) / 
                                      chartData.reduce((sum, item) => sum + item.sales, 0)) * 100 || 0).toFixed(1)}%
                                </h6>
                            </div>
                        </div>
                    </div>
                    <div className="col-md-3">
                        <div className="card bg-light">
                            <div className="card-body py-2">
                                <small>Avg Daily</small>
                                <h6 className="text-info mb-0">
                                    {formatRupiah(chartData.reduce((sum, item) => sum + item.sales, 0) / chartData.length || 0)}
                                </h6>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EnhancedSalesChart;
