import React, { useState, useEffect } from 'react';
import { productAPI, transactionAPI } from '../../utils/api';
import { formatRupiah, getCategoryLabel, getAnimalTypeLabel } from '../../utils/helpers';
import { toast } from 'react-toastify';

const TransactionForm = () => {
    const [products, setProducts] = useState([]);
    const [filteredProducts, setFilteredProducts] = useState([]);
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        product_id: '',
        type: 'out',
        qty: 1,
        date: new Date().toISOString().split('T')[0]
    });
    const [filters, setFilters] = useState({
        category: '',
        animal_type: '',
        search: ''
    });

    useEffect(() => {
        loadProducts();
    }, []);

    useEffect(() => {
        filterProducts();
    }, [products, filters]);

    const loadProducts = async () => {
        try {
            const data = await productAPI.getAll();
            setProducts(data);
        } catch (error) {
            toast.error('Gagal memuat data produk');
        }
    };

    const filterProducts = () => {
        let filtered = products;

        if (filters.category) {
            filtered = filtered.filter(product => product.category === filters.category);
        }

        if (filters.animal_type) {
            filtered = filtered.filter(product => product.animal_type === filters.animal_type);
        }

        if (filters.search) {
            filtered = filtered.filter(product =>
                product.name.toLowerCase().includes(filters.search.toLowerCase()) ||
                product.sku_code.toLowerCase().includes(filters.search.toLowerCase())
            );
        }

        setFilteredProducts(filtered);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        if (!formData.product_id) {
            toast.error('Pilih produk terlebih dahulu');
            return;
        }

        if (formData.qty <= 0) {
            toast.error('Jumlah harus lebih dari 0');
            return;
        }

        setLoading(true);

        try {
            await transactionAPI.create(formData);
            toast.success(`Transaksi ${formData.type === 'in' ? 'masuk' : 'keluar'} berhasil dicatat`);
            
            // Reset form
            setFormData({
                product_id: '',
                type: 'out',
                qty: 1,
                date: new Date().toISOString().split('T')[0]
            });
            
            // Reload products to get updated stock
            loadProducts();
        } catch (error) {
            toast.error('Gagal mencatat transaksi');
            console.error('Transaction error:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: name === 'qty' ? Number(value) : value
        }));
    };

    const handleFilterChange = (e) => {
        const { name, value } = e.target;
        setFilters(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const selectedProduct = products.find(p => p.id == formData.product_id);

    return (
        <div className="fade-in">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <h1 className="h2">üîÑ Transaksi Stok</h1>
            </div>

            <div className="row">
                {/* Transaction Form */}
                <div className="col-lg-5">
                    <div className="card shadow mb-4">
                        <div className="card-header">
                            <h6 className="m-0 font-weight-bold text-primary">
                                üìù Form Transaksi
                            </h6>
                        </div>
                        <div className="card-body">
                            <form onSubmit={handleSubmit}>
                                <div className="row">
                                    <div className="col-md-6">
                                        <div className="mb-3">
                                            <label className="form-label">Jenis Transaksi *</label>
                                            <select
                                                className="form-select"
                                                name="type"
                                                value={formData.type}
                                                onChange={handleChange}
                                                required
                                            >
                                                <option value="in">üì• Stok Masuk (Pembelian)</option>
                                                <option value="out">üì§ Stok Keluar (Penjualan)</option>
                                            </select>
                                        </div>
                                    </div>
                                    <div className="col-md-6">
                                        <div className="mb-3">
                                            <label className="form-label">Tanggal *</label>
                                            <input
                                                type="date"
                                                className="form-control"
                                                name="date"
                                                value={formData.date}
                                                onChange={handleChange}
                                                required
                                            />
                                        </div>
                                    </div>
                                </div>

                                <div className="mb-3">
                                    <label className="form-label">Jumlah *</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        name="qty"
                                        value={formData.qty}
                                        onChange={handleChange}
                                        min="1"
                                        required
                                    />
                                </div>

                                {selectedProduct && (
                                    <div className="alert alert-info">
                                        <strong>Produk Terpilih:</strong> {selectedProduct.name}
                                        <br />
                                        <strong>Stok Saat Ini:</strong> {selectedProduct.current_stock}
                                        <br />
                                        <strong>Stok Setelah Transaksi:</strong>{' '}
                                        {formData.type === 'in' 
                                            ? selectedProduct.current_stock + formData.qty
                                            : selectedProduct.current_stock - formData.qty
                                        }
                                    </div>
                                )}

                                <button
                                    type="submit"
                                    className="btn btn-petshop w-100"
                                    disabled={loading || !formData.product_id}
                                >
                                    {loading ? (
                                        <>
                                            <span className="spinner-border spinner-border-sm me-2"></span>
                                            Memproses...
                                        </>
                                    ) : (
                                        `üíæ Simpan Transaksi ${formData.type === 'in' ? 'Masuk' : 'Keluar'}`
                                    )}
                                </button>
                            </form>
                        </div>
                    </div>
                </div>

                {/* Product Selection */}
                <div className="col-lg-7">
                    <div className="card shadow">
                        <div className="card-header">
                            <h6 className="m-0 font-weight-bold text-primary">
                                üì¶ Pilih Produk
                            </h6>
                        </div>
                        <div className="card-body">
                            {/* Filters */}
                            <div className="row mb-3">
                                <div className="col-md-4">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Cari produk..."
                                        name="search"
                                        value={filters.search}
                                        onChange={handleFilterChange}
                                    />
                                </div>
                                <div className="col-md-4">
                                    <select
                                        className="form-select"
                                        name="category"
                                        value={filters.category}
                                        onChange={handleFilterChange}
                                    >
                                        <option value="">Semua Kategori</option>
                                        <option value="dry_food">Makanan Kering</option>
                                        <option value="wet_food">Makanan Basah</option>
                                        <option value="snack">Snack</option>
                                        <option value="sand">Pasir</option>
                                    </select>
                                </div>
                                <div className="col-md-4">
                                    <select
                                        className="form-select"
                                        name="animal_type"
                                        value={filters.animal_type}
                                        onChange={handleFilterChange}
                                    >
                                        <option value="">Semua Hewan</option>
                                        <option value="dog">Anjing</option>
                                        <option value="cat">Kucing</option>
                                        <option value="all">Semua</option>
                                    </select>
                                </div>
                            </div>

                            {/* Products List */}
                            <div className="products-list" style={{ maxHeight: '400px', overflowY: 'auto' }}>
                                {filteredProducts.length === 0 ? (
                                    <div className="text-center text-muted py-4">
                                        <p>Tidak ada produk yang sesuai dengan filter</p>
                                    </div>
                                ) : (
                                    <div className="row">
                                        {filteredProducts.map((product) => (
                                            <div key={product.id} className="col-md-6 mb-3">
                                                <div 
                                                    className={`card product-card cursor-pointer ${
                                                        formData.product_id == product.id ? 'border-primary' : ''
                                                    }`}
                                                    onClick={() => setFormData(prev => ({
                                                        ...prev,
                                                        product_id: product.id
                                                    }))}
                                                    style={{ cursor: 'pointer' }}
                                                >
                                                    <div className="card-body">
                                                        <h6 className="card-title">
                                                            {product.name}
                                                        </h6>
                                                        <small className="text-muted d-block">
                                                            {product.sku_code}
                                                        </small>
                                                        <small className="d-block">
                                                            {getCategoryLabel(product.category)} - {getAnimalTypeLabel(product.animal_type)}
                                                        </small>
                                                        <div className="mt-2">
                                                            <span className="badge bg-light text-dark">
                                                                Stok: {product.current_stock}
                                                            </span>
                                                            <span className="badge bg-success ms-1">
                                                                {formatRupiah(product.price_sell)}
                                                            </span>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Recent Transactions Info */}
            <div className="row mt-4">
                <div className="col-12">
                    <div className="card shadow">
                        <div className="card-header">
                            <h6 className="m-0 font-weight-bold text-primary">
                                ‚ÑπÔ∏è Informasi Transaksi
                            </h6>
                        </div>
                        <div className="card-body">
                            <div className="row text-center">
                                <div className="col-md-4">
                                    <div className="border rounded p-3 bg-light">
                                        <h4>üì•</h4>
                                        <h5>Stok Masuk</h5>
                                        <p className="mb-0">Penambahan stok dari pembelian</p>
                                    </div>
                                </div>
                                <div className="col-md-4">
                                    <div className="border rounded p-3 bg-light">
                                        <h4>üì§</h4>
                                        <h5>Stok Keluar</h5>
                                        <p className="mb-0">Pengurangan stok dari penjualan</p>
                                    </div>
                                </div>
                                <div className="col-md-4">
                                    <div className="border rounded p-3 bg-light">
                                        <h4>‚ö°</h4>
                                        <h5>Real-time</h5>
                                        <p className="mb-0">Stok otomatis terupdate</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default TransactionForm;
