import React, { useState, useEffect } from 'react';
import { productAPI, transactionAPI } from '../../utils/api';
import { formatRupiah, getStockStatus } from '../../utils/helpers';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import StockBadge from '../../components/Common/StockBadge';
import EnhancedSalesChart from '../../components/Charts/EnhancedSalesChart';
import QuickActions from '../../components/Dashboard/QuickActions';
import RecentActivities from '../../components/Dashboard/RecentActivities';
import { toast } from 'react-toastify';

const EnhancedDashboard = () => {
    const [dashboardData, setDashboardData] = useState({
        stats: {
            totalProducts: 0,
            lowStockItems: 0,
            totalSales: 0,
            monthlyGrowth: 0,
            totalProfit: 0
        },
        lowStockProducts: [],
        bestSellers: [],
        recentTransactions: []
    });
    const [loading, setLoading] = useState(true);
    const [autoRefresh, setAutoRefresh] = useState(false);

    useEffect(() => {
        loadDashboardData();
        
        if (autoRefresh) {
            const interval = setInterval(loadDashboardData, 30000); // Refresh every 30 seconds
            return () => clearInterval(interval);
        }
    }, [autoRefresh]);

    const loadDashboardData = async () => {
        try {
            setLoading(true);
            
            // Load all data with proper error handling
            const [productsResponse, lowStockResponse, bestSellersResponse, recentTransactionsResponse] = await Promise.allSettled([
                productAPI.getAll(),
                productAPI.getLowStock(),
                transactionAPI.getBestSellers(5),
                transactionAPI.getRecentTransactions(10)
            ]);

            // Extract values with fallbacks
            const products = productsResponse.status === 'fulfilled' && Array.isArray(productsResponse.value) 
                ? productsResponse.value 
                : [];

            const lowStock = lowStockResponse.status === 'fulfilled' && Array.isArray(lowStockResponse.value)
                ? lowStockResponse.value
                : [];

            const bestSellers = bestSellersResponse.status === 'fulfilled' && Array.isArray(bestSellersResponse.value)
                ? bestSellersResponse.value
                : [];

            const recentTransactions = recentTransactionsResponse.status === 'fulfilled' && Array.isArray(recentTransactionsResponse.value)
                ? recentTransactionsResponse.value
                : [];

            // Safe calculations
            const totalSales = Array.isArray(bestSellers) 
                ? bestSellers.reduce((sum, item) => {
                    if (item && typeof item.total_sold === 'number' && typeof item.price_sell === 'number') {
                        return sum + (item.total_sold * item.price_sell);
                    }
                    return sum;
                }, 0)
                : 0;

            const totalCost = Array.isArray(bestSellers)
                ? bestSellers.reduce((sum, item) => {
                    if (item && typeof item.total_sold === 'number' && typeof item.price_buy === 'number') {
                        return sum + (item.total_sold * item.price_buy);
                    }
                    return sum;
                }, 0)
                : 0;

            const totalProfit = totalSales - totalCost;

            setDashboardData({
                stats: {
                    totalProducts: products.length,
                    lowStockItems: lowStock.length,
                    totalSales: totalSales,
                    monthlyGrowth: calculateMonthlyGrowth(bestSellers),
                    totalProfit: totalProfit
                },
                lowStockProducts: lowStock,
                bestSellers: bestSellers,
                recentTransactions: recentTransactions
            });

        } catch (error) {
            console.error('Dashboard error:', error);
            toast.error('Gagal memuat data dashboard');
            
            // Set safe default values
            setDashboardData({
                stats: {
                    totalProducts: 0,
                    lowStockItems: 0,
                    totalSales: 0,
                    monthlyGrowth: 0,
                    totalProfit: 0
                },
                lowStockProducts: [],
                bestSellers: [],
                recentTransactions: []
            });
        } finally {
            setLoading(false);
        }
    };

    const calculateMonthlyGrowth = (bestSellers = []) => {
        // Simple growth calculation based on best sellers data
        if (!Array.isArray(bestSellers) || bestSellers.length === 0) {
            return 0;
        }
        
        // Simulate growth calculation based on data
        const totalItems = bestSellers.reduce((sum, item) => sum + (item.total_sold || 0), 0);
        return totalItems > 0 ? 12.5 : 0; // Sample growth percentage
    };

    const handleQuickAction = (action) => {
        switch (action) {
            case 'add_product':
                window.location.href = '/inventory';
                break;
            case 'quick_sale':
                window.location.href = '/transactions';
                break;
            case 'view_reports':
                window.location.href = '/reports';
                break;
            default:
                break;
        }
    };

    if (loading) {
        return <LoadingSpinner text="Memuat dashboard..." />;
    }

    return (
        <div className="fade-in">
            {/* Header dengan Controls */}
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <div>
                    <h1 className="h2">üìä Dashboard Overview</h1>
                    <p className="text-muted mb-0">Ringkasan performa toko hari ini</p>
                </div>
                <div className="btn-toolbar mb-2 mb-md-0">
                    <div className="btn-group me-2">
                        <button 
                            className="btn btn-sm btn-outline-secondary"
                            onClick={loadDashboardData}
                        >
                            üîÑ Refresh
                        </button>
                        <button 
                            className={`btn btn-sm ${autoRefresh ? 'btn-success' : 'btn-outline-success'}`}
                            onClick={() => setAutoRefresh(!autoRefresh)}
                        >
                            {autoRefresh ? 'üî¥ Auto Refresh ON' : 'üü¢ Auto Refresh OFF'}
                        </button>
                    </div>
                </div>
            </div>

            {/* Quick Actions */}
            <QuickActions onAction={handleQuickAction} />

            {/* Enhanced Statistics Cards */}
            <div className="row mb-4">
                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-primary shadow h-100">
                        <div className="card-body text-center">
                            <div className="text-primary mb-2">üì¶</div>
                            <h4 className="card-title">{dashboardData.stats.totalProducts}</h4>
                            <p className="card-text small">Total Produk</p>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-warning shadow h-100">
                        <div className="card-body text-center">
                            <div className="text-warning mb-2">‚ö†Ô∏è</div>
                            <h4 className="card-title">{dashboardData.stats.lowStockItems}</h4>
                            <p className="card-text small">Stok Menipis</p>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-success shadow h-100">
                        <div className="card-body text-center">
                            <div className="text-success mb-2">üí∞</div>
                            <h4 className="card-title">{formatRupiah(dashboardData.stats.totalSales)}</h4>
                            <p className="card-text small">Penjualan Bulan Ini</p>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-info shadow h-100">
                        <div className="card-body text-center">
                            <div className="text-info mb-2">üìà</div>
                            <h4 className="card-title">{dashboardData.stats.monthlyGrowth}%</h4>
                            <p className="card-text small">Pertumbuhan</p>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-danger shadow h-100">
                        <div className="card-body text-center">
                            <div className="text-danger mb-2">üí∏</div>
                            <h4 className="card-title">{formatRupiah(dashboardData.stats.totalProfit)}</h4>
                            <p className="card-text small">Total Profit</p>
                        </div>
                    </div>
                </div>

                <div className="col-xl-2 col-md-4 col-6 mb-4">
                    <div className="card stat-card border-secondary shadow h-100">
                        <div className="card-body text-center">
                            <div className="text-secondary mb-2">üèÜ</div>
                            <h4 className="card-title">{dashboardData.bestSellers.length}</h4>
                            <p className="card-text small">Produk Terlaris</p>
                        </div>
                    </div>
                </div>
            </div>

            <div className="row">
                {/* Sales Chart & Recent Activities */}
                <div className="col-xl-8">
                    <EnhancedSalesChart />
                    
                    {/* Low Stock Alert */}
                    <div className="card shadow mb-4">
                        <div className="card-header py-3 d-flex justify-content-between align-items-center">
                            <h6 className="m-0 font-weight-bold text-danger">
                                ‚ö†Ô∏è Produk Stok Menipis - Prioritas Restock
                            </h6>
                            <span className="badge bg-danger">{dashboardData.lowStockProducts.length}</span>
                        </div>
                        <div className="card-body">
                            {dashboardData.lowStockProducts.length === 0 ? (
                                <div className="text-center text-muted py-4">
                                    <p>üéâ Semua stok dalam kondisi aman!</p>
                                </div>
                            ) : (
                                <div className="row">
                                    {dashboardData.lowStockProducts.slice(0, 4).map((product) => (
                                        <div key={product.id} className="col-md-6 mb-3">
                                            <div className="card border-warning h-100">
                                                <div className="card-body">
                                                    <div className="d-flex justify-content-between align-items-start">
                                                        <div>
                                                            <h6 className="card-title">{product.name}</h6>
                                                            <small className="text-muted d-block">
                                                                SKU: {product.sku_code}
                                                            </small>
                                                            <div className="mt-2">
                                                                <span className="badge bg-warning text-dark">
                                                                    Stok: {product.current_stock}
                                                                </span>
                                                                <span className="badge bg-light text-dark ms-1">
                                                                    Minimal: {product.min_stock_threshold}
                                                                </span>
                                                            </div>
                                                        </div>
                                                        <StockBadge 
                                                            currentStock={product.current_stock}
                                                            minThreshold={product.min_stock_threshold}
                                                        />
                                                    </div>
                                                    <div className="mt-2">
                                                        <small className="text-muted">
                                                            Butuh: {Math.max(0, product.min_stock_threshold - product.current_stock)} pcs
                                                        </small>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Sidebar - Best Sellers & Quick Stats */}
                <div className="col-xl-4">
                    {/* Best Sellers */}
                    <div className="card shadow mb-4">
                        <div className="card-header py-3">
                            <h6 className="m-0 font-weight-bold text-success">
                                üèÜ Top 5 Produk Terlaris
                            </h6>
                        </div>
                        <div className="card-body">
                            {!Array.isArray(dashboardData.bestSellers) || dashboardData.bestSellers.length === 0 ? (
                                <div className="text-center text-muted py-3">
                                    <p>Belum ada data penjualan</p>
                                </div>
                            ) : (
                                <div className="list-group list-group-flush">
                                    {dashboardData.bestSellers.map((product, index) => (
                                        <div key={product.id || index} className="list-group-item px-0">
                                            <div className="d-flex align-items-center">
                                                <span className="badge bg-primary me-3">
                                                    #{index + 1}
                                                </span>
                                                <div className="flex-grow-1">
                                                    <h6 className="mb-1">{product.name || 'Unknown Product'}</h6>
                                                    <small className="text-muted">
                                                        Terjual: {product.total_sold || 0} pcs
                                                    </small>
                                                </div>
                                                <div className="text-end">
                                                    <div className="fw-bold text-success">
                                                        {formatRupiah((product.total_sold || 0) * (product.price_sell || 0))}
                                                    </div>
                                                    <small className="text-muted">
                                                        {formatRupiah(product.price_sell || 0)}/pcs
                                                    </small>
                                                </div>
                                            </div>
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Recent Activities */}
                    <RecentActivities activities={dashboardData.recentTransactions} />

                    {/* Quick Stats */}
                    <div className="card shadow">
                        <div className="card-header">
                            <h6 className="m-0 font-weight-bold text-primary">
                                üìã Quick Stats
                            </h6>
                        </div>
                        <div className="card-body">
                            <div className="row text-center">
                                <div className="col-6 mb-3">
                                    <div className="border rounded p-2">
                                        <div className="h5 mb-1 text-primary">üïí</div>
                                        <small>Hari Ini</small>
                                        <div className="fw-bold">5 Transaksi</div>
                                    </div>
                                </div>
                                <div className="col-6 mb-3">
                                    <div className="border rounded p-2">
                                        <div className="h5 mb-1 text-success">üìà</div>
                                        <small>Minggu Ini</small>
                                        <div className="fw-bold">+15%</div>
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="border rounded p-2">
                                        <div className="h5 mb-1 text-warning">‚≠ê</div>
                                        <small>Rating</small>
                                        <div className="fw-bold">4.8/5</div>
                                    </div>
                                </div>
                                <div className="col-6">
                                    <div className="border rounded p-2">
                                        <div className="h5 mb-1 text-info">üë•</div>
                                        <small>Customers</small>
                                        <div className="fw-bold">128</div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default EnhancedDashboard;
