import React, { useState, useEffect } from 'react';
import { productAPI, transactionAPI } from '../../utils/api';
import { formatRupiah, getCategoryLabel, getAnimalTypeLabel } from '../../utils/helpers';
import LoadingSpinner from '../../components/Common/LoadingSpinner';
import { toast } from 'react-toastify';

const AdvancedForecasting = () => {
    const [products, setProducts] = useState([]);
    const [forecasts, setForecasts] = useState({});
    const [loading, setLoading] = useState(true);
    const [calculating, setCalculating] = useState(false);
    const [method, setMethod] = useState('moving_average');
    const [period, setPeriod] = useState(3);

    useEffect(() => {
        loadProducts();
    }, []);

    const loadProducts = async () => {
        try {
            setLoading(true);
            const data = await productAPI.getAll();
            setProducts(data);
        } catch (error) {
            toast.error('Gagal memuat data produk');
        } finally {
            setLoading(false);
        }
    };

    const calculateForecast = async (productId) => {
        try {
            setCalculating(true);
            
            let forecast;
            switch (method) {
                case 'moving_average':
                    forecast = await calculateMovingAverage(productId);
                    break;
                case 'weighted_average':
                    forecast = await calculateWeightedAverage(productId);
                    break;
                case 'seasonal':
                    forecast = await calculateSeasonalForecast(productId);
                    break;
                default:
                    forecast = await calculateMovingAverage(productId);
            }
            
            setForecasts(prev => ({
                ...prev,
                [productId]: forecast
            }));
        } catch (error) {
            toast.error('Gagal menghitung forecast');
        } finally {
            setCalculating(false);
        }
    };

    const calculateMovingAverage = async (productId) => {
        // Simulate API call
        const salesData = [
            { month: 'Jan', sales: 45 },
            { month: 'Feb', sales: 52 },
            { month: 'Mar', sales: 48 }
        ];
        
        const total = salesData.reduce((sum, item) => sum + item.sales, 0);
        const forecast = Math.round(total / salesData.length);
        const currentStock = products.find(p => p.id === productId)?.current_stock || 0;
        const suggestion = Math.max(0, forecast - currentStock);
        
        return {
            method: 'Moving Average',
            forecast,
            suggestion,
            confidence: 'Tinggi',
            salesData,
            calculation: `(${salesData.map(s => s.sales).join(' + ')}) / ${salesData.length} = ${forecast}`
        };
    };

    const calculateWeightedAverage = async (productId) => {
        // More recent months have higher weights
        const salesData = [
            { month: 'Jan', sales: 45, weight: 1 },
            { month: 'Feb', sales: 52, weight: 2 },
            { month: 'Mar', sales: 48, weight: 3 }
        ];
        
        const weightedSum = salesData.reduce((sum, item) => sum + (item.sales * item.weight), 0);
        const totalWeight = salesData.reduce((sum, item) => sum + item.weight, 0);
        const forecast = Math.round(weightedSum / totalWeight);
        const currentStock = products.find(p => p.id === productId)?.current_stock || 0;
        const suggestion = Math.max(0, forecast - currentStock);
        
        return {
            method: 'Weighted Average',
            forecast,
            suggestion,
            confidence: 'Sedang',
            salesData,
            calculation: `Weighted average dengan bobot ${salesData.map(s => s.weight).join(', ')}`
        };
    };

    const calculateAllForecasts = async () => {
        try {
            setCalculating(true);
            const newForecasts = {};
            
            for (const product of products) {
                try {
                    const forecast = await calculateForecast(product.id);
                    newForecasts[product.id] = forecast;
                } catch (error) {
                    console.error(`Forecast error for product ${product.id}:`, error);
                }
            }
            
            setForecasts(newForecasts);
            toast.success('Forecast semua produk berhasil dihitung');
        } catch (error) {
            toast.error('Gagal menghitung forecast untuk beberapa produk');
        } finally {
            setCalculating(false);
        }
    };

    const getSuggestionColor = (suggestion) => {
        if (suggestion === 0) return 'success';
        if (suggestion <= 10) return 'warning';
        return 'danger';
    };

    const getConfidenceBadge = (confidence) => {
        const colors = {
            'Tinggi': 'success',
            'Sedang': 'warning',
            'Rendah': 'danger'
        };
        
        return colors[confidence] || 'secondary';
    };

    if (loading) {
        return <LoadingSpinner text="Memuat data forecasting..." />;
    }

    return (
        <div className="fade-in">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <div>
                    <h1 className="h2">ðŸ”® Advanced Forecasting</h1>
                    <p className="text-muted">
                        Prediksi cerdas dengan multiple algorithms
                    </p>
                </div>
                <div className="btn-toolbar mb-2 mb-md-0">
                    <button 
                        className="btn btn-sm btn-petshop"
                        onClick={calculateAllForecasts}
                        disabled={calculating || products.length === 0}
                    >
                        {calculating ? (
                            <>
                                <span className="spinner-border spinner-border-sm me-2"></span>
                                Menghitung...
                            </>
                        ) : (
                            'ðŸ“Š Hitung Semua Forecast'
                        )}
                    </button>
                </div>
            </div>

            {/* Forecasting Controls */}
            <div className="row mb-4">
                <div className="col-md-8">
                    <div className="card">
                        <div className="card-body">
                            <div className="row">
                                <div className="col-md-6">
                                    <label className="form-label">Metode Forecasting</label>
                                    <select
                                        className="form-select"
                                        value={method}
                                        onChange={(e) => setMethod(e.target.value)}
                                    >
                                        <option value="moving_average">Simple Moving Average</option>
                                        <option value="weighted_average">Weighted Moving Average</option>
                                        <option value="seasonal">Seasonal Analysis</option>
                                    </select>
                                </div>
                                <div className="col-md-6">
                                    <label className="form-label">Periode (Bulan)</label>
                                    <select
                                        className="form-select"
                                        value={period}
                                        onChange={(e) => setPeriod(parseInt(e.target.value))}
                                    >
                                        <option value={3}>3 Bulan</option>
                                        <option value={6}>6 Bulan</option>
                                        <option value={12}>12 Bulan</option>
                                    </select>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="col-md-4">
                    <div className="card bg-light">
                        <div className="card-body text-center">
                            <h6>Metode Terpilih</h6>
                            <h4 className="text-primary">
                                {method === 'moving_average' ? 'SMA' : 
                                 method === 'weighted_average' ? 'WMA' : 'Seasonal'}
                            </h4>
                            <small className="text-muted">
                                {period} bulan data historis
                            </small>
                        </div>
                    </div>
                </div>
            </div>

            {/* Products Table */}
            <div className="card shadow">
                <div className="card-body">
                    <div className="table-responsive">
                        <table className="table table-hover">
                            <thead className="table-light">
                                <tr>
                                    <th>Produk</th>
                                    <th>Stok Saat Ini</th>
                                    <th>Metode</th>
                                    <th>Forecast</th>
                                    <th>Rekomendasi</th>
                                    <th>Confidence</th>
                                    <th>Aksi</th>
                                </tr>
                            </thead>
                            <tbody>
                                {products.map((product) => {
                                    const forecast = forecasts[product.id];
                                    const hasForecast = forecast && forecast.forecast !== undefined;
                                    
                                    return (
                                        <tr key={product.id}>
                                            <td>
                                                <strong>{product.name}</strong>
                                                <br />
                                                <small className="text-muted">
                                                    {product.sku_code} â€¢ {getCategoryLabel(product.category)}
                                                </small>
                                            </td>
                                            <td>
                                                <span className="fw-bold">{product.current_stock}</span>
                                            </td>
                                            <td>
                                                {hasForecast ? (
                                                    <small className="text-info">{forecast.method}</small>
                                                ) : (
                                                    <span className="text-muted">-</span>
                                                )}
                                            </td>
                                            <td>
                                                {hasForecast ? (
                                                    <span className="fw-bold text-primary">
                                                        {forecast.forecast} pcs
                                                    </span>
                                                ) : (
                                                    <span className="text-muted">-</span>
                                                )}
                                            </td>
                                            <td>
                                                {hasForecast ? (
                                                    <span className={`badge bg-${getSuggestionColor(forecast.suggestion)}`}>
                                                        {forecast.suggestion === 0 ? 'CUKUP' : 
                                                         `BUTUH ${forecast.suggestion} pcs`}
                                                    </span>
                                                ) : (
                                                    <span className="text-muted">-</span>
                                                )}
                                            </td>
                                            <td>
                                                {hasForecast ? (
                                                    <span className={`badge bg-${getConfidenceBadge(forecast.confidence)}`}>
                                                        {forecast.confidence}
                                                    </span>
                                                ) : (
                                                    <span className="badge bg-secondary">-</span>
                                                )}
                                            </td>
                                            <td>
                                                <button
                                                    className="btn btn-outline-primary btn-sm"
                                                    onClick={() => calculateForecast(product.id)}
                                                    disabled={calculating}
                                                    title="Hitung Forecast"
                                                >
                                                    {calculating ? (
                                                        <span className="spinner-border spinner-border-sm"></span>
                                                    ) : (
                                                        'ðŸ“ˆ'
                                                    )}
                                                </button>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>

            {/* Forecasting Analysis */}
            {Object.keys(forecasts).length > 0 && (
                <div className="row mt-4">
                    <div className="col-12">
                        <div className="card shadow">
                            <div className="card-header">
                                <h6 className="m-0 font-weight-bold text-primary">
                                    ðŸ“‹ Detail Analisis Forecast
                                </h6>
                            </div>
                            <div className="card-body">
                                <div className="row">
                                    {products.filter(p => forecasts[p.id]?.salesData).map(product => {
                                        const forecast = forecasts[product.id];
                                        return (
                                            <div key={product.id} className="col-lg-6 mb-4">
                                                <div className="card h-100">
                                                    <div className="card-header">
                                                        <h6 className="mb-0">{product.name}</h6>
                                                    </div>
                                                    <div className="card-body">
                                                        <div className="row mb-3">
                                                            <div className="col-6">
                                                                <small className="text-muted">Metode</small>
                                                                <div className="fw-bold">{forecast.method}</div>
                                                            </div>
                                                            <div className="col-6">
                                                                <small className="text-muted">Confidence</small>
                                                                <div>
                                                                    <span className={`badge bg-${getConfidenceBadge(forecast.confidence)}`}>
                                                                        {forecast.confidence}
                                                                    </span>
                                                                </div>
                                                            </div>
                                                        </div>
                                                        
                                                        <table className="table table-sm table-bordered">
                                                            <thead>
                                                                <tr>
                                                                    <th>Bulan</th>
                                                                    <th>Penjualan</th>
                                                                    {forecast.method === 'Weighted Average' && <th>Weight</th>}
                                                                </tr>
                                                            </thead>
                                                            <tbody>
                                                                {forecast.salesData.map((sale, index) => (
                                                                    <tr key={index}>
                                                                        <td>{sale.month}</td>
                                                                        <td>{sale.sales} pcs</td>
                                                                        {forecast.method === 'Weighted Average' && (
                                                                            <td>{sale.weight}</td>
                                                                        )}
                                                                    </tr>
                                                                ))}
                                                            </tbody>
                                                        </table>
                                                        
                                                        <div className="alert alert-info mt-2">
                                                            <small>
                                                                <strong>Perhitungan:</strong> {forecast.calculation}
                                                            </small>
                                                        </div>
                                                    </div>
                                                </div>
                                            </div>
                                        );
                                    })}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AdvancedForecasting;
