import React, { useState, useEffect } from 'react';
import { transactionAPI, productAPI } from '../../utils/api';
import { formatRupiah, exportToExcel } from '../../utils/helpers';
import { toast } from 'react-toastify';

const Reports = () => {
    const [dateRange, setDateRange] = useState({
        start_date: new Date(new Date().getFullYear(), new Date().getMonth(), 1).toISOString().split('T')[0],
        end_date: new Date().toISOString().split('T')[0]
    });
    const [reportData, setReportData] = useState([]);
    const [loading, setLoading] = useState(false);
    const [reportGenerated, setReportGenerated] = useState(false);
    const [products, setProducts] = useState([]);

    useEffect(() => {
        loadProducts();
    }, []);

    const loadProducts = async () => {
        try {
            const data = await productAPI.getAll();
            setProducts(data);
        } catch (error) {
            console.error('Error loading products:', error);
        }
    };

    const generateReport = async () => {
        if (!dateRange.start_date || !dateRange.end_date) {
            toast.error('Pilih rentang tanggal terlebih dahulu');
            return;
        }

        if (new Date(dateRange.start_date) > new Date(dateRange.end_date)) {
            toast.error('Tanggal mulai tidak boleh lebih besar dari tanggal akhir');
            return;
        }

        setLoading(true);

        try {
            const data = await transactionAPI.getSalesReport(dateRange.start_date, dateRange.end_date);
            setReportData(data);
            setReportGenerated(true);
            toast.success('Laporan berhasil dihasilkan');
        } catch (error) {
            toast.error('Gagal menghasilkan laporan');
            console.error('Report error:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleExportExcel = () => {
        if (reportData.length === 0) {
            toast.error('Tidak ada data untuk diexport');
            return;
        }

            // Build CSV that matches the screenshot: summary block + table
            const title = 'Laporan Penjualan';
            const period = `Periode: ${new Date(dateRange.start_date).toLocaleDateString('id-ID')} - ${new Date(dateRange.end_date).toLocaleDateString('id-ID')}`;

            const summaryLines = [
                [`${title}`],
                [`${period}`],
                [],
                ['Total Barang Masuk', totals.totalStockIn],
                ['Total Barang Keluar', totals.totalStockOut],
                ['Jumlah Produk', totals.totalProducts],
                ['Total Penjualan (Rp)', totals.totalSales],
                []
            ];

            const headers = ['Nama Barang','Kode SKU','Stok Awal','Barang Masuk','Barang Keluar','Stok Akhir','Total Penjualan (Rp)'];

            const rows = reportData.map(item => ([
                item.name,
                item.sku_code,
                item.initial_stock,
                item.stock_in,
                item.stock_out,
                item.final_stock,
                item.total_sales
            ]));

            // Compose CSV content
            const escapeCell = (v) => {
                if (v === null || v === undefined) return '""';
                const s = String(v).replace(/"/g, '""');
                return `"${s}"`;
            };

            const csvLines = [];
            summaryLines.forEach(line => {
                if (line.length === 0) {
                    csvLines.push('');
                } else {
                    csvLines.push(line.map(c => escapeCell(c)).join(','));
                }
            });

            // header
            csvLines.push(headers.map(h => escapeCell(h)).join(','));
            // rows
            rows.forEach(r => {
                csvLines.push(r.map(c => escapeCell(c)).join(','));
            });

            // totals footer
            csvLines.push('');
            csvLines.push([ 'TOTAL:', '', '', totals.totalStockIn, totals.totalStockOut, '', totals.totalSales ].map(c => escapeCell(c)).join(','));

            const csvContent = csvLines.join('\n');
            const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
            const link = document.createElement('a');
            const url = URL.createObjectURL(blob);
            link.setAttribute('href', url);
            link.setAttribute('download', `laporan_penjualan_${dateRange.start_date}_${dateRange.end_date}.csv`);
            link.style.visibility = 'hidden';
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(url);

            toast.success('Data berhasil diexport (format ringkasan)');
    };

    const handlePrintPDF = () => {
        window.print();
    };

    const calculateTotals = () => {
        const totals = {
            totalStockIn: 0,
            totalStockOut: 0,
            totalSales: 0,
            totalProducts: reportData.length
        };

        reportData.forEach(item => {
            totals.totalStockIn += parseInt(item.stock_in) || 0;
            totals.totalStockOut += parseInt(item.stock_out) || 0;
            totals.totalSales += parseFloat(item.total_sales) || 0;
        });

        return totals;
    };

    const totals = calculateTotals();

    // Calculate summary statistics
    const getSummaryStats = () => {
        const bestSeller = reportData.reduce((best, current) => 
            (current.stock_out > best.stock_out) ? current : best, { stock_out: 0, name: '-' }
        );

        const highestSales = reportData.reduce((highest, current) => 
            (current.total_sales > highest.total_sales) ? current : highest, { total_sales: 0, name: '-' }
        );

        return {
            bestSeller,
            highestSales,
            averageSales: totals.totalSales / totals.totalProducts || 0
        };
    };

    const summaryStats = getSummaryStats();

    return (
        <div className="fade-in">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap align-items-center pt-3 pb-2 mb-3 border-bottom">
                <h1 className="h2">ðŸ“‹ Laporan Penjualan</h1>
                <small className="text-muted">Analisis performa penjualan dan stok</small>
            </div>

            {/* Date Range Selection */}
            <div className="row mb-4">
                <div className="col-lg-8">
                    <div className="card shadow">
                        <div className="card-header bg-primary text-white">
                            <h6 className="m-0 font-weight-bold">
                                ðŸ“… Pilih Periode Laporan
                            </h6>
                        </div>
                        <div className="card-body">
                            <div className="row align-items-end">
                                <div className="col-md-5">
                                    <div className="mb-3">
                                        <label className="form-label">Tanggal Mulai</label>
                                        <input
                                            type="date"
                                            className="form-control"
                                            value={dateRange.start_date}
                                            onChange={(e) => setDateRange(prev => ({
                                                ...prev,
                                                start_date: e.target.value
